import { Link, useNavigate } from 'react-router-dom';
import { NavbarBrand } from 'react-bootstrap';
import { useState, useEffect } from 'react';

const Navbar = (props) => {
  let Navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState(props.searchTerm);
  const [isNavHidden, setIsNavHidden] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      let lastScrollY = window.scrollY;

      if (lastScrollY < window.scrollY) {
        setIsNavHidden(true);
      } else {
        setIsNavHidden(false);
      }

      lastScrollY = window.scrollY;
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      // Cleanup the event listener on component unmount
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Empty dependency array means this effect will run once after the initial render

  return (
    <>
      <nav className={`bg-dark nav--hidden ${isNavHidden ? 'nav--hidden' : ''}`}>
        <Link className='p-3 btn btn-dark' to={'/'}>
          <NavbarBrand className='text-white'>Check Out Some Countries</NavbarBrand>
        </Link>
        <input type='text' onChange={handleInputChange} value={searchTerm} placeholder='Search for countries' />
      </nav>
    </>
  );

  function handleInputChange(e) {
    let newval = e.target.value;
    setSearchTerm(newval);
    props.onHandleChange(newval);
  }
};

export default Navbar;
