import { useParams } from "react-router-dom";
import { useState, useEffect, useCallback } from "react";
import { Row, Col, Spinner, Image, Accordion, Card } from "react-bootstrap";
import axios from "axios";

import Weather from "../components/Weather";

//////api bits
const weatherKey = `c1ae794e4fbf4a4a82f173252230111`;
const weatherUrl = `http://api.weatherapi.com/v1/current.json?key=c1ae794e4fbf4a4a82f173252230111&q=`;
const countryUrl = `https://restcountries.com/v3.1/name/`;
const borderUrl = `https://restcountries.com/v3/alpha/codes/`;

const SingleCountry = () => {
	let { name } = useParams();
	const [country, setCountry] = useState(null);
	const [border, setBorder] = useState([]);
	const [weather, setWeather] = useState(null);

	useEffect(() => {
		axios
		  .get(`https://restcountries.com/v3.1/name/${name}?fullText=true`)
		  .then((response) => {
			const countryData = response.data[0];
			setCountry(countryData);

			// Some countries didnt have borders and the page would crash on some countries

			  axios
				.get(
					(`https://restcountries.com/v3.1/alpha?codes=${countryData.borders}`)
				)
				.then((borderResponse) => {
				  setBorder(borderResponse.data);
				})
				.catch((error) => {
				  console.error(error);
				})

			  .catch((error) => {
				console.error(error);
			  });
		  }) 

	  }, [name]);



	let weatherInfo = <p>There is no weather info right now</p>;

	if (weather?.current?.condition?.text) {
		weatherInfo = (
			<div>
				<h6>
					<b>Temp:</b> {weather.current.temp_c}
				</h6>
				<h6>
					<b>Condition:</b> {weather.current.condition.text}
				</h6>
			</div>
		);
	}

	return (
		<div>
			{weather ? (
				<Row>
					<Col className="my-3">
						<Image className="w-100" src={country.flags.svg} />

						<Accordion defaultActiveKey="0" className="my-2">
							<Accordion.Item eventKey="0">
								<Accordion.Header>Bordering Nation</Accordion.Header>
								<Accordion.Body>
									{border ? (
										border.map((borderData, index) => (
											<div key={index}>
											<div >{borderData[0].name?.common}</div>
											<div >{borderData[0].flags[0]}</div>
											</div>
										))
									) : (
										<p>No borders found for this country.</p>
									)}
								</Accordion.Body>
							</Accordion.Item>
							<Accordion.Item eventKey="1">
								<Accordion.Header>Map</Accordion.Header>
								<Accordion.Body>
									<iframe
										title="map"
										width="100%"
										height="400"
										frameBorder="0"
										src={`https://www.openstreetmap.org/export/embed.html?bbox=${country.latlng[1]},${country.latlng[0]}`}
									></iframe>
								</Accordion.Body>
							</Accordion.Item>
						</Accordion>
					</Col>
					<Col className="my-3">
						<h1 className="bd-title mb-4">
							<b> {country.name.common}</b>
						</h1>
						<h5>
							<b>Official Name:</b> {country.name.official}
						</h5>
						<h5>
							<b>Continent :</b> {country.region}
						</h5>
						<h5>
							<b>Subregion :</b> {country.subregion}
						</h5>

						<Col>
							<h2>
								<b>Weather</b>
							</h2>
							<div>{weatherInfo}</div>
						</Col>
					</Col>
				</Row>
			) : (
				<Spinner animation="grow" />
			)}
		</div>
	);
};

export default SingleCountry;
