import { useParams } from "react-router-dom";
import { useState, useEffect, useCallback } from "react";
import { Row, Col, Spinner, Image, Accordion, Card } from "react-bootstrap";
import axios from "axios";

import Weather from "../components/Weather";

const weatherKey = `c1ae794e4fbf4a4a82f173252230111`;
const weatherUrl = `http://api.weatherapi.com/v1/current.json?key=c1ae794e4fbf4a4a82f173252230111&q=`;
const countryUrl = `https://restcountries.com/v3.1/name/`;
const borderUrl = `https://restcountries.com/v3/alpha/`;

const SingleCountry = () => {
  let { name } = useParams();
  const [country, setCountry] = useState(null);
  const [border, setBorder] = useState(null);
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null); // Added state for error handling

  const Borders = useCallback(() => {
    let borderData = [];

    if (country && country.borders != null) {
      for (let x = 0; x < country.borders.length; x++) {
        axios
        .get(`${borderUrl}${country.borders[x]}?fullText=true`)
        .then((borderResponse) => {
            borderData.push(borderResponse.data);
            setBorder(borderData);
        })
        .catch((borderError) => {
            setError(`Error fetching border data: ${borderError.message}`);
        });
    }
    }
  }, [country]);

  useEffect(() => {
    axios
      .get(`${countryUrl}${name}?fullText=true`)
      .then((countryResponse) => {
        const countryData = countryResponse.data[0];
        setCountry(countryData);

        axios
          .get(`${weatherUrl}${name}&aqi=no.`)
          .then((weatherResponse) => {
            const weatherData = weatherResponse.data;
            setWeather(weatherData);
          })
          .catch((weatherError) => {
            setError(`Error fetching weather data: ${weatherError.message}`);
          });
      })
      .catch((error) => {
        setError(`Error fetching country data: ${error.message}`);
      });

    Borders();
  }, [name, Borders]);

  let weatherInfo = <p>There is no weather info right now</p>;

  if (weather?.current?.condition?.text) {
    weatherInfo = (
      <div>
        <h6>
          <b>Temp:</b> {weather.current.temp_c}
        </h6>
        <h6>
          <b>Condition:</b> {weather.current.condition.text}
        </h6>
      </div>
    );
  }

  return (
    <div>
      {error ? ( // Display error message if there's an error
        <div>{error}</div>
      ) : weather ? (
        <Row>
          <Col className="my-3">
            <Image className="w-100" src={country.flags.svg} />

            <Accordion defaultActiveKey="0" className="my-2">
              <Accordion.Item eventKey="0">
                <Accordion.Header>Bordering Nation</Accordion.Header>
                <Accordion.Body>
                  {country.borders ? (
                    country.borders.map((border, index) => (
                      <div key={index}>{border}</div>
                    ))
                  ) : (
                    <p>No borders found for this country.</p>
                  )}
                </Accordion.Body>
              </Accordion.Item>
              <Accordion.Item eventKey="1">
                <Accordion.Header>Map</Accordion.Header>
                <Accordion.Body>
                  <iframe
                    title="map"
                    width="100%"
                    height="400"
                    frameBorder="0"
                    src={`https://www.openstreetmap.org/export/embed.html?bbox=${country.latlng[1]},${country.latlng[0]}`}
                  ></iframe>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </Col>
          <Col className="my-3">
            <h1 className="bd-title mb-4">
              <b> {country.name.common}</b>
            </h1>
            <h5>
              <b>Official Name:</b> {country.name.official}
            </h5>
            <h5>
              <b>Continent :</b> {country.region}
            </h5>
            <h5>
              <b>Subregion :</b> {country.subregion}
            </h5>

            <Col>
              <h2>
                <b>Weather</b>
              </h2>
              <div>{weatherInfo}</div>
            </Col>
          </Col>
        </Row>
      ) : (
        <Spinner animation="grow" />
      )}
    </div>
  );
};

export default SingleCountry;
